import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Paper,
  Button,
  TextField,
  Typography,
  Dialog,
  Container,
  Grid,
  DialogContent
} from '@mui/material';
import ViewNominee from './ViewNominee'; // Ensure this component is correctly imported if used for viewing more details

function NomineeDetails() {
    const { nomineeId } = useParams();
    const navigate = useNavigate();
    const [details, setDetails] = useState([]);
    const [nomineeName, setNomineeName] = useState('');
    const [openDialog, setOpenDialog] = useState(false);

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            if (response.data.length > 0) {
                setNomineeName(response.data[0].nominee_name);  // Assuming the first entry is the nominee's name
                setDetails(response.data.slice(1));  // The rest are the details
            }
        })
        .catch(error => {
            console.error('Error fetching nominee details:', error);
            navigate('/dashboard'); // Redirect or handle error
        });
    }, [nomineeId, navigate]);

    const handleDialogOpen = () => {
        setOpenDialog(true);
    };

    const handleDialogClose = () => {
        setOpenDialog(false);
    };

    const handleBack = () => {
        navigate(-1); // Navigate back to the previous page
    };

    return (
        <Paper style={{ padding: 20 }}>
            <Button variant="outlined" onClick={handleBack} style={{ marginBottom: 20 }}>
                Go Back
            </Button>
            <Typography variant="h5" gutterBottom>
                Details for Nominee: <Button onClick={handleDialogOpen} style={{
                    textDecoration: 'underline',
                    color: 'blue',
                    cursor: 'pointer'
                }}>
                    {nomineeName}
                </Button>
            </Typography>
            <Container>
                <Grid container spacing={2}>
                    {details.map((detail, index) => (
                        <React.Fragment key={index}>
                            {[
                                { key: 'ic_q1', question: 'Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company:', value: detail.ic_q1 },
                                { key: 'ic_q2', question: 'List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services:', value: detail.ic_q2 },
                                { key: 'ic_q3', question: 'Comparing with people in this position:', value: detail.ic_q3 },
                                { key: 'ic_q4', question: 'What’s the expansion of job responsibility:', value: detail.ic_q4 },
                                { key: 'ic_q5', question: 'What is the future development area, what’s manager’s plan for the career development:', value: detail.ic_q5 }
                            ].map(field => (
                                <Grid item xs={12} key={field.key}>
                                    <TextField
                                        label={`${field.question}`}
                                        value={field.value}
                                        fullWidth
                                        InputProps={{ readOnly: true }}
                                        variant="outlined"
                                        margin="normal"
                                        multiline
                                        minRows={3}
                                    />
                                </Grid>
                            ))}
                        </React.Fragment>
                    ))}
                </Grid>
            </Container>
            <Dialog open={openDialog} onClose={handleDialogClose} fullWidth maxWidth="sm">
                <DialogContent>
                    <ViewNominee id={nomineeId} onClose={handleDialogClose} />
                </DialogContent>
            </Dialog>
        </Paper>
    );
}

export default NomineeDetails;
