import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const NominatorDashboard = () => {
    const [nominees, setNominees] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/nominee/dashboard')
          .then(response => {
            setNominees(response.data.all_nominees_nominator || []);
          })
          .catch(error => {
            console.error('Error fetching nominees:', error);
          });
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('userToken');
        navigate('/');
    };

    const confirmDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this nominee?")) {
            axios.delete(`http://127.0.0.1:5000/nominee/${id}/delete`, {
                headers: {
                    Authorization: `Bearer ${token}` // Ensure 'token' is correctly populated
                }
            })
            .then(response => {
                console.log("Nominee deleted successfully", response);
                // You might want to update your state or UI here
            })
            .catch(error => {
                console.error("Error deleting nominee:", error);
                // Handle error in UI
            });
        }
    };

    return (
      <div>
        <header>
          {/* Header content */}
          <h1>Nominator Dashboard</h1>
        </header>
        <main role="main">
          <div className="row">
            <table className="table">
              <thead>
                <tr>
                  <th>Nominee Name</th>
                  <th>Nominator</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {nominees && nominees.length > 0 ? (
                  nominees.map(nominee => (
                    <tr key={nominee.id}>
                        <td>{nominee.first_name} {nominee.last_name}</td>
                        <td>{nominee.nominator ? `${nominee.nominator.first_name} ${nominee.nominator.last_name}` : 'No Nominator'}</td>
                        <td>
                            <Link to={`/nominee/${nominee.id}/edit`}>Edit</Link>
                            <button className="text-danger" onClick={() => confirmDelete(nominee.id)}>Delete</button>
                            <Link to={`/nominee/${nominee.id}/info`}>View</Link>
                        </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="3">No nominees found or still loading...</td>
                  </tr>
                )}
              </tbody>
            </table>
            <div>
              <Link to="/nominee/new" className="btn btn-success">Submit New Nominee</Link>
            </div>
          </div>
          <div className="col-md-4">
            <Link to="/dashboard" className="btn btn-warning">Back</Link>
            <button onClick={handleLogout} className="btn btn-danger">Logout</button>
          </div>
        </main>
      </div>
    );
};

export default NominatorDashboard;
