import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Typography, Container, Box } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom'; // To use with MUI Link

const Dashboard = () => {
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://127.0.0.1:5000/user/profile', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('authToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      if(data.first_name) {
        setUserName(data.first_name);
      } else {
        throw new Error('User not found');
      }
    })
    .catch(error => {
      console.error('Error:', error);
      navigate('/'); // Redirect to login if the user data cannot be fetched
    });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Clear the stored token on logout
    navigate('/'); // Redirect to the login page
  };

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>Welcome to the Dashboard</Typography>
      <Box display="flex" justifyContent="center" mb={2}>
        <Typography variant="h5">Welcome, {userName || 'User'}!</Typography>
      </Box>
      <Button variant="contained" color="error" onClick={handleLogout}>Logout</Button>
      <Box display="flex" flexDirection="column" alignItems="center" mt={4}>
        <Typography variant="h6" component={RouterLink} to="/nominee/dashboard">
          Nominator Dashboard
        </Typography>
        <Typography variant="h6" component={RouterLink} to="/recommender/dashboard">
          Recommender Dashboard
        </Typography>
        <Typography variant="h6" component={RouterLink} to="/review/dashboard">
          Review Committee Dashboard
        </Typography>
      </Box>
    </Container>
  );
};

export default Dashboard;
