import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Typography,
} from '@mui/material';

function RecommendNewNominee() {
    const [nominees, setNominees] = useState([]);
    const [selectedNominee, setSelectedNominee] = useState('');
    const [workContributions, setWorkContributions] = useState({
        individual_contributor: false,
        project_manager: false,
        people_manager: false
    });
    const [answers, setAnswers] = useState({
        ic_q1: '',
        ic_q2: '',
        ic_q3: '',
        ic_q4: '',
        ic_q5: '',
        pm_question: '',
        pep_question: ''
    });
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/nominee/dashboard')
            .then(response => {
                setNominees(response.data.all_nominees_nominator || []);
            })
            .catch(error => console.error('Error fetching nominees:', error));
    }, []);

    const handleInputChange = (e) => {
        setAnswers({
            ...answers,
            [e.target.name]: e.target.value
        });
    };

    const handleCheckboxChange = (e) => {
        setWorkContributions({
            ...workContributions,
            [e.target.name]: e.target.checked
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const token = localStorage.getItem('authToken');
        const workContributionsToSend = Object.entries(workContributions)
            .filter(([key, value]) => value)
            .map(([key]) => key);

        const dataToSend = {
            nominee_id: selectedNominee,
            work_contributions: workContributionsToSend,
            answers
        };

        axios.post('http://127.0.0.1:5000/recommendation/create', dataToSend, {
            headers: { 
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'  
            }
        })
        .then(response => {
            console.log('Recommendation submitted:', response.data);
            navigate('/recommender/dashboard');
        })
        .catch(error => {
            console.error('Error submitting recommendation:', error);
        });
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>Recommend New Nominee</Typography>
            <form onSubmit={handleSubmit}>
                <FormControl fullWidth margin="normal">
                    <InputLabel id="nominee-label">Select Nominee</InputLabel>
                    <Select
                        labelId="nominee-label"
                        id="all_nominees"
                        value={selectedNominee}
                        label="Select Nominee"
                        onChange={e => setSelectedNominee(e.target.value)}
                    >
                        {nominees.map(nominee => (
                            <MenuItem key={nominee.id} value={nominee.id}>{nominee.first_name} {nominee.last_name}</MenuItem>
                        ))}
                    </Select>
                </FormControl>

                <FormGroup>
                    <FormControlLabel control={<Checkbox checked={workContributions.individual_contributor} onChange={handleCheckboxChange} name="individual_contributor" />} label="Individual Contributor" />
                    <FormControlLabel control={<Checkbox checked={workContributions.project_manager} onChange={handleCheckboxChange} name="project_manager" />} label="Project Manager" />
                    <FormControlLabel control={<Checkbox checked={workContributions.people_manager} onChange={handleCheckboxChange} name="people_manager" />} label="People Manager" />
                </FormGroup>

                {workContributions.individual_contributor && (
                    <>
                        <TextField fullWidth label="Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company." variant="outlined" name="ic_q1" value={answers.ic_q1} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services." variant="outlined" name="ic_q2" value={answers.ic_q2} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="Comparing with people in this position." variant="outlined" name="ic_q3" value={answers.ic_q3} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="What’s the expansion of job responsibility." variant="outlined" name="ic_q4" value={answers.ic_q4} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="What is the future development area, what’s manager’s plan for the career development." variant="outlined" name="ic_q5" value={answers.ic_q5} onChange={handleInputChange} margin="normal" />
                    </>
                )}

                {workContributions.project_manager && (
                    <>
                        <TextField fullWidth label="Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company." variant="outlined" name="ic_q1" value={answers.ic_q1} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services." variant="outlined" name="ic_q2" value={answers.ic_q2} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="Comparing with people in this position." variant="outlined" name="ic_q3" value={answers.ic_q3} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="What’s the expansion of job responsibility." variant="outlined" name="ic_q4" value={answers.ic_q4} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="What is the future development area, what’s manager’s plan for the career development." variant="outlined" name="ic_q5" value={answers.ic_q5} onChange={handleInputChange} margin="normal" />
                    </>
                )}

                {workContributions.people_manager && (
                    <>
                        <TextField fullWidth label="Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company." variant="outlined" name="ic_q1" value={answers.ic_q1} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services." variant="outlined" name="ic_q2" value={answers.ic_q2} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="Comparing with people in this position." variant="outlined" name="ic_q3" value={answers.ic_q3} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="What’s the expansion of job responsibility." variant="outlined" name="ic_q4" value={answers.ic_q4} onChange={handleInputChange} margin="normal" />
                        <TextField fullWidth label="What is the future development area, what’s manager’s plan for the career development." variant="outlined" name="ic_q5" value={answers.ic_q5} onChange={handleInputChange} margin="normal" />
                    </>
                )}

                <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>Submit New Nominee</Button>
                <Button variant="contained" onClick={() => navigate('/recommender/dashboard')} sx={{ mt: 2, ml: 2 }}>Back</Button>
            </form>
        </div>
    );
}

export default RecommendNewNominee;
