import './App.css';
import { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    first_name:'',
    isRegister: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const endpoint = formData.isRegister ? 'http://127.0.0.1:5000/user/create' : 'http://127.0.0.1:5000/user/login';
    fetch(endpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((resp) => {
        if (!resp.ok) {
          throw new Error(formData.isRegister ? 'Failed to register' : 'Failed to sign in');
        }
        // Handle successful response if needed
        console.log(formData.isRegister ? 'Registered successfully' : 'Signed in successfully');
        // Redirect or handle the sign-in/register success here
      })
      .catch((error) => console.error(error));
  };

  return (
    <div className="App">
      <h1>{formData.isRegister ? 'Register' : 'Sign In'}</h1>
      <form onSubmit={handleSubmit}>
        {/* Form inputs */}
        <input
          type="text"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
        />
        {/* Toggle between sign-in/register */}
        <button type="button" onClick={() => setFormData(prevState => ({ ...prevState, isRegister: !prevState.isRegister }))}>
          {formData.isRegister ? 'Sign In' : 'Register'}
        </button>
        <button type="submit">{formData.isRegister ? 'Register' : 'Sign In'}</button>
      </form>
    </div>
  );
}

export default App;
