import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import axios from 'axios';
import {
  Button,
  TextField,
  Typography,
  Container,
  Card,
  CardContent,
  Grid,
  IconButton
} from '@mui/material';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

function NomineeDetails() {
    const { nomineeId } = useParams();
    const navigate = useNavigate();
    const [details, setDetails] = useState([]);
    const [nomineeName, setNomineeName] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            console.log("API Response:", response.data); // Debug: check the API response structure
            if (response.data && response.data.length > 0) {
                setNomineeName(response.data[0].nominee_name);
                setDetails(response.data.slice(1));
            } else {
                console.log('No data or insufficient data returned from API');
            }
        })
        .catch(error => {
            console.error('Error fetching nominee details:', error);
            navigate('/dashboard'); // Redirect or handle error
        });
    }, [nomineeId, navigate]);

    const handleBack = () => {
        navigate(-1);
    };

    const goToNext = () => {
        setCurrentIndex((prev) => (prev + 1) % details.length);
    };

    const goToPrevious = () => {
        setCurrentIndex((prev) => prev === 0 ? details.length - 1 : prev - 1);
    };

    return (
        <Container maxWidth="md">
            <Card raised>
                <CardContent>
                    <Typography variant="h4" gutterBottom>
                        Details for Nominee: 
                        <RouterLink to={`/nominee/${nomineeId}/info`} style={{ textDecoration: 'none', color: 'blue', marginLeft: '5px' }}>
                            {nomineeName}
                        </RouterLink>
                    </Typography>
                    <Button variant="contained" color="primary" onClick={handleBack} sx={{ mb: 2 }}>
                        Go Back
                    </Button>
                    <Typography variant="h6" gutterBottom>
                        Recommender's Feedback by {details[currentIndex]?.first_name || 'Unknown'} {details[currentIndex]?.last_name || 'Recommender'}:
                    </Typography>
                    <form>
                        <Grid container spacing={2}>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company."
                                    value={details[currentIndex]?.ic_q1 || ''}
                                    InputProps={{
                                        readOnly: true,
                                    }}
                                    variant="outlined"
                                    margin="normal"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services."
                                    value={details[currentIndex]?.ic_q2 || ''}
                                    InputProps={{
                                        readOnly: true,
                                    }}
                                    variant="outlined"
                                    margin="normal"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="Comparing with people in this position."
                                    value={details[currentIndex]?.ic_q3 || ''}
                                    InputProps={{
                                        readOnly: true,
                                    }}
                                    variant="outlined"
                                    margin="normal"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="What’s the expansion of job responsibility."
                                    value={details[currentIndex]?.ic_q4 || ''}
                                    InputProps={{
                                        readOnly: true,
                                    }}
                                    variant="outlined"
                                    margin="normal"
                                />
                            </Grid>
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="What is the future development area, what’s manager’s plan for the career development."
                                    value={details[currentIndex]?.ic_q5 || ''}
                                    InputProps={{
                                        readOnly: true,
                                    }}
                                    variant="outlined"
                                    margin="normal"
                                />
                            </Grid>
                            <Grid item container justifyContent="space-between" xs={12}>
                                <IconButton onClick={goToPrevious}>
                                    <ArrowBackIosIcon />
                                </IconButton>
                                <IconButton onClick={goToNext}>
                                    <ArrowForwardIosIcon />
                                </IconButton>
                            </Grid>
                        </Grid>
                    </form>
                </CardContent>
            </Card>
        </Container>
    );
}

export default NomineeDetails;
