import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  TextField,
  Button,
  Typography,
  Container,
  Box
} from '@mui/material';

function EditNominee() {
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        department_name: '',
        job_category: '',
        email: '',
        nominator_qualification: ''
    });
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
            .then(response => {
                setFormData(response.data);
            })
            .catch(error => {
                console.error('Error fetching nominee details:', error);
                alert('Failed to fetch nominee details');
            });
    }, [id]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const authToken = localStorage.getItem('authToken'); // Ensure this key matches what you set during login

        axios.put(`http://127.0.0.1:5000/nominee/${id}/update`, formData, {
            headers: {
                'Authorization': `Bearer ${authToken}`,
                'Content-Type': 'application/json'
            }
        })
        .then(response => {
            console.log('Nominee updated successfully:', response.data);
            alert('Nominee updated successfully');
            navigate('/nominee/dashboard');
        })
        .catch(error => {
            console.error('Failed to update nominee:', error);
            alert('Failed to update nominee: ' + (error.response && error.response.data.error ? error.response.data.error : 'Unknown error'));
        });
    };

    return (
        <Container component="main" maxWidth="sm">
            <Box
                sx={{
                    marginTop: 8,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}
            >
                <Typography component="h1" variant="h5">
                    Edit Nominee
                </Typography>
                <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="first_name"
                        label="First Name"
                        name="first_name"
                        autoComplete="first_name"
                        autoFocus
                        value={formData.first_name}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="last_name"
                        label="Last Name"
                        name="last_name"
                        autoComplete="last_name"
                        value={formData.last_name}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="department_name"
                        label="Department Name"
                        name="department_name"
                        autoComplete="department_name"
                        value={formData.department_name}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="job_category"
                        label="Job Category"
                        name="job_category"
                        autoComplete="job_category"
                        value={formData.job_category}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="email"
                        label="Email Address"
                        name="email"
                        autoComplete="email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        name="nominator_qualification"
                        label="Nominator Qualification"
                        id="nominator_qualification"
                        autoComplete="nominator_qualification"
                        multiline
                        rows={4}
                        value={formData.nominator_qualification}
                        onChange={handleChange}
                    />
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        sx={{ mt: 3, mb: 2 }}
                    >
                        Update Nominee
                    </Button>
                </Box>
            </Box>
        </Container>
    );
}

export default EditNominee;
