import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

function ViewNominee() {
    const [nominee, setNominee] = useState(null);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
            .then(response => {
                setNominee(response.data);
            })
            .catch(error => {
                console.error('Error fetching nominee details:', error);
                navigate('/nominee/dashboard'); // Redirect if the fetch fails
            });
    }, [id, navigate]);

    if (!nominee) {
        return <div>Loading...</div>;
    }

    // Function to handle the back button click
    const goBack = () => {
        navigate(-1); // Go back to the previous page
    };

    return (
        <div>
            <h2>{nominee.first_name} {nominee.last_name}</h2>
            <p>Department: {nominee.department_name}</p>
            <p>Job Category: {nominee.job_category}</p>
            <p>Email: {nominee.email}</p>
            <p>Nominator Qualification: {nominee.nominator_qualification}</p>
            <button onClick={goBack}>Back</button> {/* Back button */}
            <Link to="/nominee/dashboard">Back to Dashboard</Link>
        </div>
    );
}

export default ViewNominee;
