import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const [formData, setFormData] = useState({
    // Initialize your form data state here
    name: '', // Example: nominee name
    email: '', // Example: nominee email
    // Add other fields as needed
  });

  useEffect(() => {
    // Make POST request to create nominee when formData changes
    fetch('http://127.0.0.1:5000/nominee/new', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData), // Send formData as JSON string
    })
      .then((resp) => {
        if (!resp.ok) {
          throw new Error('Failed to create nominee');
        }
        // Handle successful response if needed
        console.log('Nominee created successfully');
      })
      .catch((error) => console.error(error));
  }, [formData]); // Trigger useEffect when formData changes

  // Handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  // Handle form submission (optional)
  const handleSubmit = (e) => {
    e.preventDefault();
    // You can add form validation logic here if needed
  };

  return (
    <div className="App">
      <h1>Flask and React JS Course</h1>
      <form onSubmit={handleSubmit}>
        {/* Form inputs */}
        <input
          type="text"
          name="name"
          placeholder="Nominee Name"
          value={formData.name}
          onChange={handleChange}
        />
        <input
          type="text"
          name="email"
          placeholder="Nominee Email"
          value={formData.email}
          onChange={handleChange}
        />
        {/* Add other inputs as needed */}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
