const NomineeEducationForm = () => {
  const { id } = useParams(); // Get the nominee ID from the URL
  const navigate = useNavigate();
  const { authToken } = useAuth();
  const [educations, setEducations] = useState([]);

  useEffect(() => {
    const fetchEducationData = async () => {
      if (!authToken) {
        console.log("No authentication token available.");
        return;
      }
      try {
        const response = await axios.get(`http://127.0.0.1:5000/nominee/${id}/education`, {
          headers: { 'Authorization': `Bearer ${authToken}` }
        });
        const educationData = response.data;
        if (educationData && educationData.length > 0) {
          setEducations(educationData);
        } else {
          // Initialize with one empty form if no education data exists
          setEducations([{
            college_name: '',
            location: '',
            degree: '',
            program: '',
            graduation_year: ''
          }]);
        }
      } catch (error) {
        console.error('Failed to fetch education data:', error.response ? error.response.data : "No response");
      }
    };
    fetchEducationData();
  }, [id, authToken]);

  const handleChange = (index, e) => {
    const newEducations = educations.map((education, i) => {
      if (i === index) {
        return { ...education, [e.target.name]: e.target.value };
      }
      return education;
    });
    setEducations(newEducations);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!authToken) {
      console.error("No authentication token found.");
      return;
    }
    try {
      await Promise.all(educations.map(education => 
        axios.post(`http://127.0.0.1:5000/nominee/${id}/education/add`, education, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${authToken}`
          }
        })
      ));
      console.log('All educations added');
      navigate('/nominee/dashboard');
    } catch (error) {
      console.error('Error adding education:', error.response ? error.response.data : "No response");
    }
  };

  const addEducationForm = () => {
    setEducations([...educations, {
      college_name: '',
      location: '',
      degree: '',
      program: '',
      graduation_year: ''
    }]);
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h6" gutterBottom>Add Education Details</Typography>
      <form onSubmit={handleSubmit}>
        {educations.map((education, index) => (
          <div key={index}>
            <TextField
              fullWidth
              label="College Name"
              name="college_name"
              value={education.college_name}
              onChange={e => handleChange(index, e)}
              variant="outlined"
              margin="normal"
            />
            <TextField
              fullWidth
              label="Location"
              name="location"
              value={education.location}
              onChange={e => handleChange(index, e)}
              variant="outlined"
              margin="normal"
            />
            <TextField
              fullWidth
              label="Degree"
              name="degree"
              value={education.degree}
              onChange={e => handleChange(index, e)}
              variant="outlined"
              margin="normal"
            />
            <TextField
              fullWidth
              label="Program"
              name="program"
              value={education.program}
              onChange={e => handleChange(index, e)}
              variant="outlined"
              margin="normal"
            />
            <TextField
              fullWidth
              type="date"
              label="Graduation Year"
              name="graduation_year"
              value={education.graduation_year}
              onChange={e => handleChange(index, e)}
              variant="outlined"
              margin="normal"
              InputLabelProps={{
                shrink: true,
              }}
            />
            {educations.length - 1 === index && (
              <Button onClick={addEducationForm} variant="contained" color="primary" style={{ marginTop: '10px' }}>
                Add Another Education
              </Button>
            )}
          </div>
        ))}
        <Box display="flex" justifyContent="space-between" mt={2}>
          <Button type="submit" variant="contained" color="primary">Submit All Educations</Button>
          <Button variant="contained" color="secondary" onClick={() => navigate('/nominee/dashboard')}>Cancel</Button>
        </Box>
      </form>
    </Container>
  );
};

export default NomineeEducationForm;
