import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, FormControl, InputLabel, Select, MenuItem, FormGroup, FormHelperText } from '@mui/material';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    email: '',
    pw: '',
    first_name: '',
    last_name: '',
    confirm_pw: '',
    role: '',
    isLogin: false // Determines if the current form is for login or registration
  });
  const navigate = useNavigate(); // Used for redirecting to another route

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value // Update form data based on user input
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = formData.isLogin ? '/user/login' : '/user/create'; // Endpoint depends on whether user is logging in or registering
    try {
      const response = await axios.post(`http://127.0.0.1:5000${endpoint}`, formData);
      console.log('User action complete:', response.data);

      if (response.data.access_token) {
        localStorage.setItem('authToken', response.data.access_token);
      }

      navigate('/dashboard');
    } catch (error) {
      console.error('Error:', error.response.data); // Log the error response
    }
  };

  const toggleForm = () => {
    setFormData({
      ...formData,
      isLogin: !formData.isLogin // Toggle the boolean value to switch forms
    });
  };

  return (
    <div style={{ padding: 20 }}>
      <FormGroup>
        <h2>{formData.isLogin ? 'Login' : 'Register'}</h2>
        <TextField
          label="Email"
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          label="Password"
          type="password"
          name="pw"
          value={formData.pw}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        {!formData.isLogin && (
          <>
            <TextField
              label="First Name"
              type="text"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Last Name"
              type="text"
              name="last_name"
              value={formData.last_name}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Confirm Password"
              type="password"
              name="confirm_pw"
              value={formData.confirm_pw}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel>Role</InputLabel>
              <Select
                name="role"
                value={formData.role}
                onChange={handleChange}
                label="Role"
              >
                <MenuItem value="Nominator">Nominator</MenuItem>
                <MenuItem value="Recommender">Recommender</MenuItem>
                <MenuItem value="ReviewCommittee">ReviewCommittee</MenuItem>
              </Select>
            </FormControl>
          </>
        )}
        <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ mt: 2 }}>
          {formData.isLogin ? 'Login' : 'Register'}
        </Button>
        <Button color="secondary" onClick={toggleForm} sx={{ mt: 2 }}>
          {formData.isLogin ? 'Switch to Register' : 'Switch to Login'}
        </Button>
      </FormGroup>
    </div>
  );
}

export default RegistrationForm;
