import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button, TextField, Typography, Container, Box } from '@mui/material';
import { useAuth } from './AuthContext'; // Correctly import useAuth from AuthContext.js

const NomineeEducationForm = () => {
  const { id } = useParams(); // Use 'id' since it's the path variable you are using in your route
  const navigate = useNavigate();
  const { authToken } = useAuth(); // Destructure authToken directly from useAuth
  const [educationData, setEducationData] = useState({
    college_name: '',
    location: '',
    degree: '',
    program: '',
    graduation_year: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEducationData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!authToken) {
      console.error("No authentication token found.");
      return;
    }
    try {
      const response = await axios.post(`http://127.0.0.1:5000/nominee/${id}/education/add`, educationData, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}` // Ensure Authorization header is included
        }
      });
      console.log('Education added:', response.data);
      navigate('/nominee/dashboard'); // Redirect to dashboard or confirmation page
    } catch (error) {
      console.error('Error adding education:', error.response ? error.response.data : "No response");
    }
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h6" gutterBottom>Add Education Details</Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="College Name"
          name="college_name"
          value={educationData.college_name}
          onChange={handleChange}
          variant="outlined"
          margin="normal"
        />
        <TextField
          fullWidth
          label="Location"
          name="location"
          value={educationData.location}
          onChange={handleChange}
          variant="outlined"
          margin="normal"
        />
        <TextField
          fullWidth
          label="Degree"
          name="degree"
          value={educationData.degree}
          onChange={handleChange}
          variant="outlined"
          margin="normal"
        />
        <TextField
          fullWidth
          label="Program"
          name="program"
          value={educationData.program}
          onChange={handleChange}
          variant="outlined"
          margin="normal"
        />
        <TextField
          fullWidth
          type="date"
          label="Graduation Year"
          name="graduation_year"
          value={educationData.graduation_year}
          onChange={handleChange}
          variant="outlined"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <Box display="flex" justifyContent="space-between" mt={2}>
          <Button type="submit" variant="contained" color="primary">Add Education</Button>
          <Button variant="contained" color="secondary" onClick={() => navigate('/nominee/dashboard')}>Cancel</Button>
        </Box>
      </form>
    </Container>
  );
};

export default NomineeEducationForm;
