import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link as RouterLink } from 'react-router-dom';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography, Button, TextField } from '@mui/material';

function ReviewCommitteeDashboard() {
    const [nominees, setNominees] = useState([]);
    const [rankedNominees, setRankedNominees] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            try {
                const { data } = await axios.get('http://127.0.0.1:5000/review/dashboard', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
                });
                console.log(data);  // Debug: Review fetched data
                setNominees(data.all_nominees || []);
                setRankedNominees(data.ranked_nominees || []);
            } catch (error) {
                console.error('Failed to fetch data:', error);
            }
        };
        fetchData();
    }, []);

    const handleBack = () => navigate('/dashboard');

    const handleRankChange = (nomineeId, rank) => {
        setNominees(nominees.map(nominee =>
            nominee.id === nomineeId ? { ...nominee, rankRev: rank } : nominee
        ));
    };

    const handleSubmitRank = async (nomineeId, rank) => {
        try {
            const response = await axios.post('/api/updateRankRev', { nomineeId, rank });
            console.log(response.data.message);
            // Optionally re-fetch data here to ensure UI consistency
        } catch (error) {
            console.error('Error updating rank:', error);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <Typography variant="h4" gutterBottom>Review Committee Dashboard</Typography>
            <Button variant="contained" color="primary" onClick={handleBack} style={{ marginBottom: '20px' }}>Back to Main Dashboard</Button>

            <NomineesTable nominees={nominees} onRankChange={handleRankChange} onSubmitRank={handleSubmitRank} />
            <RankedNomineesTable rankedNominees={rankedNominees} />
        </div>
    );
}

function NomineesTable({ nominees, onRankChange, onSubmitRank }) {
    return (
        <React.Fragment>
            <Typography variant="h5" gutterBottom>All Nominees</Typography>
            <TableContainer component={Paper}>
                <Table aria-label="Nominees table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Nominee Name</TableCell>
                            <TableCell>Department</TableCell>
                            <TableCell>Job Category</TableCell>
                            <TableCell>Rank</TableCell>
                            <TableCell>Action</TableCell>
                            <TableCell>Details</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {nominees.map(nominee => (
                            <TableRow key={nominee.id}>
                                <TableCell>{nominee.first_name} {nominee.last_name}</TableCell>
                                <TableCell>{nominee.department_name}</TableCell>
                                <TableCell>{nominee.job_category}</TableCell>
                                <TableCell>
                                    <TextField
                                        type="number"
                                        value={nominee.rankRev || ''}
                                        onChange={e => onRankChange(nominee.id, parseInt(e.target.value, 10))}
                                    />
                                </TableCell>
                                <TableCell>
                                    <Button variant="contained" color="primary" onClick={() => onSubmitRank(nominee.id, nominee.rankRev)}>
                                        Submit
                                    </Button>
                                </TableCell>
                                <TableCell>
                                    <Button component={RouterLink} to={`/review/nominee/${nominee.id}/details`}>View Details</Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </React.Fragment>
    );
}

function RankedNomineesTable({ rankedNominees }) {
    return (
        <React.Fragment>
            <Typography variant="h5" gutterBottom>Ranked Nominees</Typography>
            <TableContainer component={Paper}>
                <Table aria-label="Ranked nominees table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Nominee Name</TableCell>
                            <TableCell>Department</TableCell>
                            <TableCell>Job Category</TableCell>
                            <TableCell>Rank</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rankedNominees.map(nominee => (
                            <TableRow key={nominee.id}>
                                <TableCell>{nominee.first_name} {nominee.last_name}</TableCell>
                                <TableCell>{nominee.department_name}</TableCell>
                                <TableCell>{nominee.job_category}</TableCell>
                                <TableCell>
                                    <TextField
                                        type="number"
                                        value={nominee.rankRev || ''}
                                        InputProps={{ readOnly: true }}
                                    />
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </React.Fragment>
    );
}

export default ReviewCommitteeDashboard;
