import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button, Typography, Container } from '@mui/material';

function ViewNominee({ id, onClose }) {
    const [nominee, setNominee] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
            .then(response => {
                setNominee(response.data);
            })
            .catch(error => {
                console.error('Error fetching nominee details:', error);
                navigate('/'); // Redirect if the fetch fails
            });
    }, [id, navigate]);

    if (!nominee) {
        return <div>Loading...</div>;
    }


    return (
        <Container>
            <Typography variant="h4">{nominee.first_name} {nominee.last_name}</Typography>
            <Typography variant="subtitle1">Department: {nominee.department_name}</Typography>
            <Typography variant="subtitle1">Job Category: {nominee.job_category}</Typography>
            <Typography variant="subtitle1">Email: {nominee.email}</Typography>
            <Typography variant="subtitle1">Nominator Qualification: {nominee.nominator_qualification}</Typography>

            <Button
                variant="contained"
                color="primary"
                onClick={onClose}
                style={{ marginTop: '20px' }}
            >
                Close
            </Button>
        </Container>
    );
}

export default ViewNominee;
