import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; // Ensure axios is imported

const Dashboard = () => {
  const [userName, setUserName] = useState('User'); // Initialize with default name
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/user/data')
      .then(response => {
        if (response.data.first_name) {
          setUserName(response.data.first_name);
        }
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        // Optionally handle the error, e.g., redirect to login
      });
  }, []); // Dependency array is empty, so this runs once on mount

  const handleLogout = () => {
    localStorage.removeItem('userToken'); // Adjust to your token key
    navigate('/');
  };

  return (
    <div>
      <h1>Welcome to Dashboard</h1>
      <div className="d-flex justify-content-around">
        <h2>Welcome, {userName}!</h2>
      </div>
      <button onClick={handleLogout} className="btn btn-link text-danger">Logout</button>
      <div className="d-flex justify-content-center">
        <h1><Link to="/nominee/dashboard">Nominator Dashboard</Link></h1>
      </div>
      <div className="d-flex justify-content-center">
        <h1><Link to="/recommender/dashboard">Recommender Dashboard</Link></h1>
      </div>
      <div className="d-flex justify-content-center">
        <h1><Link to="/review/dashboard">Review Committee Dashboard</Link></h1>
      </div>
    </div>
  );
};

export default Dashboard;
