import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Paper, Typography, List, ListItem, ListItemText, Divider, Button } from '@mui/material';

function NomineeDetails() {
    const { nomineeId } = useParams();
    const navigate = useNavigate();
    const [details, setDetails] = useState([]);
    const [nomineeName, setNomineeName] = useState('');  // State to store the nominee's name

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            setDetails(response.data);
            // Assume the first detail item contains the nominee's name or adjust as per your API structure
            if (response.data.length > 0) {
                setNomineeName(`${response.data[0].first_name} ${response.data[0].last_name}`);
            }
        })
        .catch(error => console.error('Error fetching nominee details:', error));
    }, [nomineeId]);

    const handleBack = () => {
        navigate(-1); // Navigates back to the previous page
    };

    return (
        <Paper style={{ padding: 20 }}>
            <Button variant="outlined" onClick={handleBack} style={{ marginBottom: 20 }}>
                Go Back
            </Button>
            <Typography variant="h5" gutterBottom>Details for Nominee: {nomineeId}</Typography>
            <List>
                {details.map((detail, index) => (
                    <React.Fragment key={index}>
                        <ListItem alignItems="flex-start">
                            <ListItemText
                                primary="Recommender's Feedback:"
                                secondary={
                                    <>
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company:
                                            <br />{detail.ic_q1}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services:
                                            <br />{detail.ic_q2}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            Comparing with people in this position:
                                            <br />{detail.ic_q3}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            What’s the expansion of job responsibility:
                                            <br />{detail.ic_q4}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            What is the future development area, what’s manager’s plan for the career development:
                                            <br />{detail.ic_q5}
                                        </Typography>
                                        <br />
                                    </>
                                }
                            />
                        </ListItem>
                        {index < details.length - 1 && <Divider variant="inset" component="li" />}
                    </React.Fragment>
                ))}
            </List>
        </Paper>
    );
}

export default NomineeDetails;
