import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Paper, Typography, List, ListItem, ListItemText, Divider } from '@mui/material';

function NomineeDetails() {
    const { nomineeId } = useParams();
    const [details, setDetails] = useState([]);

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            setDetails(response.data);
        })
        .catch(error => console.error('Error fetching nominee details:', error));
    }, [nomineeId]);

    return (
        <Paper style={{ padding: 20 }}>
            <Typography variant="h5" gutterBottom>Details for Nominee ID: {nomineeId}</Typography>
            <List>
                {details.map((detail, index) => (
                    <React.Fragment key={index}>
                        <ListItem alignItems="flex-start">
                            <ListItemText
                                primary="Recommender's Feedback:"
                                secondary={
                                    <>
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company: 
                                            {detail.ic_q1}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services: {detail.ic_q2}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            Comparing with people in this position: {detail.ic_q3}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            What’s the expansion of job responsibility: {detail.ic_q4}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            What is the future development area, what’s manager’s plan for the career development: {detail.ic_q5}
                                        </Typography>
                                    </>
                                }
                            />
                        </ListItem>
                        {index < details.length - 1 && <Divider variant="inset" component="li" />}
                    </React.Fragment>
                ))}
            </List>
        </Paper>
    );
}

export default NomineeDetails;
