import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function EditNominee() {
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        department_name: '',
        job_category: '',
        email: '',
        nominator_qualification: ''
    });
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
            .then(response => {
                // Ensure that the data structure received matches the expected format
                const { nominee } = response.data;
                setFormData(nominee || {}); // Handle null or undefined nominee
            })
            .catch(error => {
                console.error('Error fetching nominee details:', error);
            });
    }, [id]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const authToken = localStorage.getItem('token'); // Ensure the token is correctly retrieved

        axios.put(`http://127.0.0.1:5000/nominee/${id}/update`, formData, {
            headers: {
                'Authorization': `Bearer ${authToken}`
            }
        })
        .then(response => {
            alert('Nominee updated successfully');
            navigate('/nominee/dashboard');
        })
        .catch(error => {
            console.error('Failed to update nominee:', error);
            alert('Failed to update nominee: ' + (error.response && error.response.data.error ? error.response.data.error : 'Unknown error'));
        });
    };

    return (
        <div>
            <h2>Edit Nominee</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="first_name" value={formData.first_name || ''} onChange={handleChange} placeholder="First Name" />
                <input type="text" name="last_name" value={formData.last_name || ''} onChange={handleChange} placeholder="Last Name" />
                <input type="text" name="department_name" value={formData.department_name || ''} onChange={handleChange} placeholder="Department Name" />
                <input type="text" name="job_category" value={formData.job_category || ''} onChange={handleChange} placeholder="Job Category" />
                <input type="email" name="email" value={formData.email || ''} onChange={handleChange} placeholder="Email" />
                <textarea name="nominator_qualification" value={formData.nominator_qualification || ''} onChange={handleChange} placeholder="Nominator Qualification"></textarea>
                <button type="submit">Update Nominee</button>
            </form>
        </div>
    );
}

export default EditNominee;
