import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Container, Typography, TextField, Button, Select, MenuItem, FormControl, InputLabel
} from '@mui/material';

function RecommendNominee() {
  const [nominees, setNominees] = useState([]);
  const [selectedNominee, setSelectedNominee] = useState('');
  const [contribution, setContribution] = useState('');
  const navigate = useNavigate();

  // Fetch nominees
  useEffect(() => {
    axios.get('http://localhost:5000/nominee/dashboard', {
      headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
    })
    .then(response => {
      setNominees(response.data.all_nominees_nominator || []);
    })
    .catch(error => {
      console.error('Error fetching nominees:', error);
    });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post('http://localhost:5000/recommendation/create', {
      nominee_id: selectedNominee,
      work_contributions: contribution
    }, {
      headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
    })
    .then(() => {
      navigate('/recommender/dashboard');
    })
    .catch(error => {
      console.error('Error submitting recommendation:', error);
    });
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h6">Recommend New Nominee</Typography>
      <form onSubmit={handleSubmit}>
        <FormControl fullWidth>
          <InputLabel id="nominee-label">Select Nominee</InputLabel>
          <Select
            labelId="nominee-label"
            id="nominee-select"
            value={selectedNominee}
            label="Select Nominee"
            onChange={e => setSelectedNominee(e.target.value)}
          >
            {nominees.map((nominee) => (
              <MenuItem key={nominee.id} value={nominee.id}>{nominee.first_name} {nominee.last_name}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <TextField
          fullWidth
          label="Work Contributions"
          variant="outlined"
          value={contribution}
          onChange={e => setContribution(e.target.value)}
          margin="normal"
        />
        <Button type="submit" variant="contained" color="primary">
          Submit Recommendation
        </Button>
      </form>
    </Container>
  );
}

export default RecommendNominee;
