import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ViewNominee = () => {
    const [nominee, setNominee] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { id } = useParams();  // Get the id from URL

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
            .then(response => {
                setNominee(response.data);
                setLoading(false);
            })
            .catch(error => {
                setError('Failed to fetch nominee details');
                setLoading(false);
            });
    }, [id]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;
    if (!nominee) return <div>No Nominee Found</div>;

    return (
        <div>
            <h1>Nominee Details</h1>
            <p><strong>Name:</strong> {nominee.first_name} {nominee.last_name}</p>
            <p><strong>Department:</strong> {nominee.department_name}</p>
            <p><strong>Job Category:</strong> {nominee.job_category}</p>
            <p><strong>Email:</strong> {nominee.email}</p>
            <p><strong>Qualifications:</strong> {nominee.nominator_qualification}</p>
        </div>
    );
};

export default ViewNominee;
