import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Paper,
  Typography,
  Button,
  Dialog,
  TextField,
  Grid,
  Container,
  DialogContent,
  IconButton
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

function NomineeDetails() {

    const { nomineeId } = useParams();
    const navigate = useNavigate();
    const [details, setDetails] = useState([]);
    const [nomineeName, setNomineeName] = useState('');
    const [currentIndex, setCurrentIndex] = useState(0);
    const [openDialog, setOpenDialog] = useState(false);

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            setNomineeName(response.data[0].nominee_name); // Assuming the first entry is the nominee's name
            setDetails(response.data.slice(1)); // The rest are the details
        })
        .catch(error => {
            console.error('Error fetching nominee details:', error);
            navigate('/dashboard'); // Redirect or handle error
        });
    }, [nomineeId, navigate]);

    const handleNext = () => {
        if (currentIndex < details.length - 1) {
            setCurrentIndex(currentIndex + 1);
        }
    };

    const handlePrevious = () => {
        if (currentIndex > 0) {
            setCurrentIndex(currentIndex - 1);
        }
    };

    const handleDialogOpen = () => {
        setOpenDialog(true);
    };

    const handleDialogClose = () => {
        setOpenDialog(false);
    };

    const handleBack = () => {
        navigate(-1); // Navigate back to the previous page
    };

    const detail = details[currentIndex] || {};

    return (
        <Container maxWidth="md">
            <Paper style={{ padding: 20 }}>
                <Button startIcon={<ArrowBackIcon />} onClick={handleBack} style={{ marginBottom: 20 }}>
                    Go Back
                </Button>
                <Typography variant="h5" gutterBottom>
                    Details for Nominee: <Button onClick={handleDialogOpen} style={{
                        textDecoration: 'underline',
                        color: 'blue',
                        cursor: 'pointer'
                    }}>
                        {nomineeName}
                    </Button>
                </Typography>
                <Grid container spacing={2}>
                    {[
                        { key: 'ic_q1', label: 'Importance of accomplishments' },
                        { key: 'ic_q2', label: 'Technical patents and reports' },
                        { key: 'ic_q3', label: 'Comparing with peers' },
                        { key: 'ic_q4', label: 'Expansion of job responsibility' },
                        { key: 'ic_q5', label: 'Future development area' }
                    ].map(field => (
                        <Grid item xs={12} key={field.key}>
                            <TextField
                                label={field.label}
                                value={detail[field.key] || ''}
                                fullWidth
                                InputProps={{ readOnly: true }}
                                variant="outlined"
                                margin="normal"
                                multiline
                                minRows={2}
                            />
                        </Grid>
                    ))}
                </Grid>
                <Grid container justifyContent="space-between" style={{ marginTop: '10px' }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handlePrevious}
                        disabled={currentIndex === 0}
                    >
                        Previous
                    </Button>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleNext}
                        disabled={currentIndex >= details.length - 1}
                    >
                        Next
                    </Button>
                </Grid>
                <Dialog open={openDialog} onClose={handleDialogClose} fullWidth maxWidth="sm">
                    <DialogContent>
                        {/* Optionally display more nominee details */}
                    </DialogContent>
                </Dialog>
            </Paper>
        </Container>
    );
}

export default NomineeDetails;
