import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link as RouterLink } from 'react-router-dom';
import { Button, Table, TableBody, TableCell, TableHead, TableRow, Container, Typography } from '@mui/material';

const NominatorDashboard = () => {
    const [nominees, setNominees] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/nominee/dashboard')
          .then(response => {
            setNominees(response.data.all_nominees_nominator || []);
          })
          .catch(error => {
            console.error('Error fetching nominees:', error);
          });
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('userToken');
        navigate('/');
    };

    const confirmDelete = (id) => {
        const token = localStorage.getItem('authToken');
        if (window.confirm("Are you sure you want to delete this nominee?")) {
            axios.delete(`http://127.0.0.1:5000/nominee/${id}/delete`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            .then(response => {
                console.log("Nominee deleted successfully", response);
                // Optionally update your state or UI here
            })
            .catch(error => {
                console.error("Error deleting nominee:", error);
                // Optionally handle error in UI
            });
        }
    };

    return (
      <Container>
        <Typography variant="h4" gutterBottom>Nominator Dashboard</Typography>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Nominee Name</TableCell>
              <TableCell>Nominator</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {nominees.length > 0 ? nominees.map(nominee => (
              <TableRow key={nominee.id}>
                <TableCell>{nominee.first_name} {nominee.last_name}</TableCell>
                <TableCell>{nominee.nominator ? `${nominee.nominator.first_name} ${nominee.nominator.last_name}` : 'No Nominator'}</TableCell>
                <TableCell>
                  <Button component={RouterLink} to={`/nominee/${nominee.id}/edit`}>Edit</Button>
                  <Button color="error" onClick={() => confirmDelete(nominee.id)}>Delete</Button>
                  <Button component={RouterLink} to={`/nominee/${nominee.id}/info`}>View</Button>
                </TableCell>
              </TableRow>
            )) : (
              <TableRow>
                <TableCell colSpan="3">No nominees found or still loading...</TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
        <Button variant="contained" color="primary" component={RouterLink} to="/nominee/new">Submit New Nominee</Button>
        <Button variant="outlined" onClick={handleLogout}>Logout</Button>
        <Button variant="outlined" color="secondary" onClick={() => navigate('/dashboard')} style={{ margin: '20px' }}>
          Back
        </Button>
      </Container>
    );
};

export default NominatorDashboard;
