import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [firstName, setFirstName] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetching user data from the /dashboard endpoint
        const response = await axios.get('/dashboard');
        const userData = response.data;

        // Extracting the first name from the user data
        const { all_users } = userData;
        if (all_users.length > 0) {
          const { full_name } = all_users[0]; // Assuming the first user's full name is the current user's full name
          const firstName = full_name.split(' ')[0];
          setFirstName(firstName);
        }
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <div className="row">
        <div className="d-flex justify-content-around">
          <h2>Welcome: {firstName}</h2>
        </div>
      </div>

      <a className="text-danger" href="/user/logout"> Logout </a>

      <div className="d-flex justify-content-center">
        <h1><a href="/nominee/dashboard"> Nominator Dashboard </a></h1>
      </div>

      <div className="d-flex justify-content-center">
        <h1><a href="/recommender/dashboard"> Recommender Dashboard</a></h1>
      </div>

      <div className="d-flex justify-content-center">
        <h1><a href="/review/dashboard"> Review Committee Dashboard</a></h1>
      </div>
    </div>
  );
}

export default Dashboard;
