import React, { useState } from 'react';
import axios from 'axios';

const NewNomineeForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    department_name: '',
    job_category: '',
    email: '',
    nominator_qualification: ''
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('http://127.0.0.1:5000/nominee/create', formData, {
      headers: {
        Authorization: `Bearer ${authToken}` // Include the authentication token in the request headers
      }
    })
      .then(response => {
        // Handle success
        console.log('Nominee created:', response.data);
        // Redirect or show a success message
      })
      .catch(error => {
        // Handle error
        console.error('Error creating nominee:', error);
        // Show an error message to the user
      });
  };

  return (
    <div className="container">
      <div className="col-14 p-2">
        <legend className="d-flex justify-content-around">New Nominee Form Information</legend>
      </div>
      <div className="d-flex justify-content-center">
        <form onSubmit={handleSubmit} className="bg-light text-dark">
          {/* Form fields */}
          <div className="form-group">
            <label>Nominee First Name</label>
            <input
              type="text"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
              className="form-control"
              placeholder="Nominee First Name"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <label>Nominee Last Name</label>
            <input
              type="text"
              name="last_name"
              value={formData.last_name}
              onChange={handleChange}
              className="form-control"
              placeholder="Nominee Last Name"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <label>Department Name</label>
            <input
              type="text"
              name="department_name"
              value={formData.department_name}
              onChange={handleChange}
              className="form-control"
              placeholder="Department Name"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <label>Job Category</label>
            <input
              type="text"
              name="job_category"
              value={formData.job_category}
              onChange={handleChange}
              className="form-control"
              placeholder="Job Category"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              placeholder="Email"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <label>Nominator Qualification</label>
            <textarea
              name="nominator_qualification"
              value={formData.nominator_qualification}
              onChange={handleChange}
              className="form-control"
              placeholder="Your Project Description"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-success">Submit New Nominee</button>
          </div>
        </form>
      </div>
      <div className="col-md-4">
        <a href="/nominee/dashboard" className="btn btn-warning">Back</a>
      </div>
    </div>
  );
};

export default NewNomineeForm;
