import React, { createContext, useContext, useState } from 'react';
import { Navigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {

  const navigate = useNavigate();

  const [authState, setAuthState] = useState({
    authToken: localStorage.getItem('authToken'),
    role: localStorage.getItem('userRole') // Verify this key is the same used when saving
  });

  const login = (token, userRole) => {
    localStorage.setItem('authToken', token);
    localStorage.setItem('userRole', userRole);  // Ensure these keys match
    setAuthState({ authToken: token, role: userRole });
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userRole'); // Ensure these keys match
    setAuthState({ authToken: null, role: null });
    navigate('/', { replace: true });
    window.location.reload();
  };

  return (
    <AuthContext.Provider value={{ ...authState, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
