import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Paper, Typography, List, ListItem, ListItemText, Divider, Button, Dialog } from '@mui/material';
import ViewNominee from './ViewNominee';  // Ensure this is imported correctly

function NomineeDetails() {
    const { nomineeId } = useParams();
    const [details, setDetails] = useState([]);
    const [nomineeName, setNomineeName] = useState('');
    const [openDialog, setOpenDialog] = useState(false);  // State to manage Dialog visibility

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            if (response.data.length > 0) {
                setNomineeName(response.data[0].nominee_name);  // Assuming nominee_name is available
                setDetails(response.data);
            }
        })
        .catch(error => console.error('Error fetching nominee details:', error));
    }, [nomineeId]);

    const handleDialogOpen = () => {
        setOpenDialog(true);
    };

    const handleDialogClose = () => {
        setOpenDialog(false);
    };

    return (
        <Paper style={{ padding: 20 }}>
            <Typography variant="h5" gutterBottom>
                Details for Nominee: <Button onClick={handleDialogOpen} style={{
                    textDecoration: 'underline',
                    color: 'blue',
                    cursor: 'pointer'
                }}>
                    {nomineeName}
                </Button>
            </Typography>
            <List>
                {details.map((detail, index) => (
                    <React.Fragment key={index}>
                        <ListItem alignItems="flex-start">
                            <ListItemText
                                primary="Recommender's Feedback:"
                                secondary={
                                    <>
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            {detail.ic_q1}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            {detail.ic_q2}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            {detail.ic_q3}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            {detail.ic_q4}
                                        </Typography>
                                        <br />
                                        <Typography component="span" variant="body2" color="textPrimary" display="block">
                                            {detail.ic_q5}
                                        </Typography>
                                        <br />
                                    </>
                                }
                            />
                        </ListItem>
                        {index < details.length - 1 && <Divider variant="inset" component="li" />}
                    </React.Fragment>
                ))}
            </List>
            <Dialog open={openDialog} onClose={handleDialogClose} fullWidth maxWidth="sm">
                <ViewNominee id={nomineeId} onClose={handleDialogClose} />
            </Dialog>
        </Paper>
    );
}

export default NomineeDetails;
