import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [authState, setAuthState] = useState({
    authToken: localStorage.getItem('authToken'),
    userId: localStorage.getItem('userId'),
    role: localStorage.getItem('userRole')  // Store and manage user role
  });

  const login = (token, id, role) => {
    localStorage.setItem('authToken', token);
    localStorage.setItem('userId', id);
    localStorage.setItem('userRole', role);  // Save role in local storage
    setAuthState({ authToken: token, userId: id, role });
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userId');
    localStorage.removeItem('userRole');
    setAuthState({ authToken: null, userId: null, role: null });
  };

  useEffect(() => {
    console.log("Auth context setup:", authState);
  }, [authState]);

  return (
    <AuthContext.Provider value={{ authToken, userId, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
