import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function RecommenderDashboard() {
    const [nominees, setNominees] = useState([]);
    const [currentUser, setCurrentUser] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch user and nominee data
        const fetchData = async () => {
          try {
              const userRes = await axios.get('http://localhost:5000/recommender/dashboard');
              setCurrentUser(userRes.data);

              const nomineesRes = await axios.get('http://localhost:5000/nominees/nominator');
              setNominees(nomineesRes.data);
          } catch (error) {
              console.error('Error fetching data:', error);
          }
      };

        fetchData();
    }, []);

    const handleLogout = () => {
        // Perform logout
        navigate('/');
    };

    return (
        <div>
            <header className="navbar navbar-dark bg-dark box-shadow">
                <div className="container d-flex justify-content-between">
                    <strong>Welcome Recommender: {currentUser.full_name}</strong>
                </div>
            </header>

            <div className="container">
                <a className="text-success" href="/recommend/new">+ Recommend New Nominee Form</a>
                <a href="/dashboard">Back</a>
                <button className="text-danger" onClick={handleLogout}>Logout</button>

                <div className="row">
                    <h2>List of Nominees to Recommend</h2>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Nominee Name</th>
                                <th>Nominator</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {nominees.map(nominee => (
                                <tr key={nominee.id}>
                                    <td>{nominee.first_name} {nominee.last_name}</td>
                                    <td>{nominee.nominator.full_name}</td>
                                    <td>
                                        <a className="btn btn-success" href={`/nominee/${nominee.id}/info`}>View</a>
                                        <a className="btn btn-warning" href="/recommend/new">+ Recommend</a>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}

export default RecommenderDashboard;
