import React, { useState } from 'react';
import axios from 'axios';

const NewNomineeForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    department_name: '',
    job_category: '',
    email: '',
    nominator_qualification: ''
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('/nominee/create', formData)
      .then(response => {
        // Handle success
        console.log('Nominee created:', response.data);
        // Redirect or show a success message
      })
      .catch(error => {
        // Handle error
        console.error('Error creating nominee:', error);
        // Show an error message to the user
      });
  };

  return (
    <div className="container">
      <div className="col-14 p-2">
        <legend className="d-flex justify-content-around">New Nominee Form Information</legend>
      </div>
      <div className="d-flex justify-content-center">
        <form onSubmit={handleSubmit} className="bg-light text-dark">
          {/* Form fields */}
          <div className="form-group">
            <label>Nominee First Name</label>
            <input
              type="text"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
              className="form-control"
              placeholder="Nominee First Name"
            />
            {/* Validation messages */}
          </div>
          {/* Repeat similar structure for other form fields */}
          <div className="form-group">
            <label>What qualifications and attributes distinguish you as a suitable nominator for endorsing this nominee?</label>
            <textarea
              name="nominator_qualification"
              value={formData.nominator_qualification}
              onChange={handleChange}
              className="form-control"
              placeholder="Your Project Description"
            />
            {/* Validation messages */}
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-success">Submit New Nominee</button>
          </div>
        </form>
      </div>
      <div className="col-md-4">
        <a href="/nominee/dashboard" className="btn btn-warning">Back</a>
      </div>
    </div>
  );
};

export default NewNomineeForm;
