import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Import useAuth hook from AuthContext

function RegistrationForm() {
  const [formData, setFormData] = useState({
    isLogin: false
  });
  const navigate = useNavigate();
  const { login } = useAuth(); // Destructure login function from useAuth

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Form Data:', formData);
      const endpoint = formData.isLogin ? '/user/login' : '/user/create';
      const response = await axios.post(`http://127.0.0.1:5000${endpoint}`, formData);
      console.log('User action complete:', response.data);
      // After successful login or registration, call the login function
      if (formData.isLogin) {
        login(); // Call the login function from the authentication context
      }
      // Redirect to dashboard after successful registration or login
      navigate('/dashboard');
    } catch (error) {
      console.error('Error:', error.response);
      // Optionally, handle error response (e.g., display error messages)
    }
  };

  const toggleForm = () => {
    setFormData({
      ...formData,
      isLogin: !formData.isLogin
    });
  };

  return (
    <div className="d-flex justify-content-around">
      <form onSubmit={handleSubmit} className="col-4 p-4 bg-dark text-light">
        <h2 className="text-primary">{formData.isLogin ? 'Login' : 'Register'}</h2>
        {!formData.isLogin && (
          <>
            <input type="text" name="first_name" placeholder="First Name" value={formData.first_name} onChange={handleChange} className="form-control" />
            <input type="text" name="last_name" placeholder="Last Name" value={formData.last_name} onChange={handleChange} className="form-control" />
            <input type="password" name="confirm_pw" placeholder="Confirm Password" value={formData.confirm_pw} onChange={handleChange} className="form-control" />
            <select name="role" value={formData.role} onChange={handleChange} className="form-control">
              <option value="">Select Role</option>
              <option value="Nominator">Nominator</option>
              <option value="Recommender">Recommender</option>
              <option value="ReviewCommittee">ReviewCommittee</option>
            </select>
          </>
        )}
        <button type="submit" className="btn btn-primary">{formData.isLogin ? 'Login' : 'Register'}</button>
        <button type="button" onClick={toggleForm} className="btn btn-link">{formData.isLogin ? 'Switch to Register' : 'Switch to Login'}</button>
      </form>
    </div>
  );
}

export default RegistrationForm;
