import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [registerFormData, setRegisterFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    pw: '',
    confirm_pw: '',
    role: ''
  });

  const [loginFormData, setLoginFormData] = useState({
    email: '',
    pw: ''
  });

  const handleRegisterChange = (e) => {
    setRegisterFormData({
      ...registerFormData,
      [e.target.name]: e.target.value
    });
  };

  const handleLoginChange = (e) => {
    setLoginFormData({
      ...loginFormData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/user/create', registerFormData);
      console.log('User registered:', response.data);
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/user/login', loginFormData);
      console.log('User logged in:', response.data);
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  return (
    <div className="d-flex justify-content-around">
      <form onSubmit={handleRegisterSubmit} className="col-4 p-4 bg-dark text-light">
        <h2 className="text-primary">Register</h2>
        {/* Render input fields for registration */}
        <input type="text" name="first_name" placeholder="First Name" value={registerFormData.first_name} onChange={handleRegisterChange} className="form-control" />
        {/* Render other input fields similarly */}
        <button type="submit" className="btn btn-primary">Register</button>
      </form>

      <form onSubmit={handleLoginSubmit} className="col-4 p-4 bg-dark text-light">
        <h2 className="text-success">Login</h2>
        {/* Render input fields for login */}
        <input type="text" name="email" placeholder="Email" value={loginFormData.email} onChange={handleLoginChange} className="form-control" />
        {/* Render other input fields similarly */}
        <button type="submit" className="btn btn-success">Login</button>
      </form>
    </div>
  );
}

export default App;
