import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Button,
  TextField,
  Typography,
  Container,
  Card,
  CardContent,
  Grid
} from '@mui/material';

function NomineeDetails() {
    const { nomineeId } = useParams();
    const navigate = useNavigate();
    const [details, setDetails] = useState([]);
    const [nomineeName, setNomineeName] = useState('');

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/review/nominee/${nomineeId}/details`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
        })
        .then(response => {
            if (response.data.length > 0) {
                setNomineeName(response.data[0].nominee_name);  // Assuming the first entry is the nominee's name
                setDetails(response.data.slice(1));  // The rest are the details
            }
        })
        .catch(error => {
            console.error('Error fetching nominee details:', error);
            navigate('/dashboard'); // Redirect or handle error
        });
    }, [nomineeId, navigate]);

    const handleBack = () => {
        navigate(-1); // Navigate back to the previous page
    };

    return (
        <Container maxWidth="md">
            <Card raised>
                <CardContent>
                    <Typography variant="h4" gutterBottom>
                        Details for Nominee: {nomineeName}
                    </Typography>
                    <Button variant="contained" color="primary" onClick={handleBack} sx={{ mb: 2 }}>
                        Go Back
                    </Button>
                    <form>
                        <Grid container spacing={2}>
                            {details.map((detail, index) => (
                                <React.Fragment key={index}>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Personally, became aware of the importance of his/her extraordinary accomplishments and their impact on the company."
                                            value={detail.ic_q1}
                                            InputProps={{
                                                readOnly: true,
                                            }}
                                            variant="outlined"
                                            margin="normal"
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="List technical patents; technical reports and presentations; development of products, applications and systems; and application of facilities and services."
                                            value={detail.ic_q2}
                                            InputProps={{
                                                readOnly: true,
                                            }}
                                            variant="outlined"
                                            margin="normal"
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Comparing with people in this position."
                                            value={detail.ic_q3}
                                            InputProps={{
                                                readOnly: true,
                                            }}
                                            variant="outlined"
                                            margin="normal"
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="What’s the expansion of job responsibility."
                                            value={detail.ic_q4}
                                            InputProps={{
                                                readOnly: true,
                                            }}
                                            variant="outlined"
                                            margin="normal"
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="What is the future development area, what’s manager’s plan for the career development."
                                            value={detail.ic_q5}
                                            InputProps={{
                                                readOnly: true,
                                            }}
                                            variant="outlined"
                                            margin="normal"
                                        />
                                    </Grid>
                                </React.Fragment>
                            ))}
                        </Grid>
                    </form>
                </CardContent>
            </Card>
        </Container>
    );
}

export default NomineeDetails;
