import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/user/profile', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('authToken')}`
      }
    })
    .then(response => {
      setUserName(response.data.first_name);
    })
    .catch(error => {
      console.error('Failed to fetch user profile:', error);
      navigate('/');
    });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/');
  };

  return (
    <div>
      <h1>Welcome to Dashboard</h1>
      <div className="d-flex justify-content-around">
        <h2>Welcome, {userName || "User"}!</h2>
      </div>
      <button onClick={handleLogout} className="btn btn-link text-danger">Logout</button>
      <div className="d-flex justify-content-center">
        <h1><Link to="/nominee/dashboard">Nominator Dashboard</Link></h1>
      </div>
      <div className="d-flex justify-content-center">
        <h1><Link to="/recommender/dashboard">Recommender Dashboard</Link></h1>
      </div>
      <div className="d-flex justify-content-center">
        <h1><Link to="/review/dashboard">Review Committee Dashboard</Link></h1>
      </div>
    </div>
  );
};

export default Dashboard;
