import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import {
  Button, Typography, Dialog, DialogTitle, DialogContent, DialogActions
} from '@mui/material';

function ViewNominee({ open, onClose }) {
    const [nominee, setNominee] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        if (open) {
            axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
                .then(response => {
                    setNominee(response.data);
                })
                .catch(error => {
                    console.error('Error fetching nominee details:', error);
                    if (onClose) onClose(); // Close dialog if fetch fails
                });
        }
    }, [id, open, onClose]);

    if (!nominee) {
        return <div>Loading...</div>;
    }

    return (
        <Dialog open={open} onClose={onClose} aria-labelledby="nominee-details-dialog-title">
            <DialogTitle id="nominee-details-dialog-title">{nominee.first_name} {nominee.last_name}</DialogTitle>
            <DialogContent>
                <Typography variant="subtitle1">Department: {nominee.department_name}</Typography>
                <Typography variant="subtitle1">Job Category: {nominee.job_category}</Typography>
                <Typography variant="subtitle1">Email: {nominee.email}</Typography>
                <Typography variant="subtitle1">Nominator Qualification: {nominee.nominator_qualification}</Typography>
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="primary">
                    Close
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default ViewNominee;
