import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography, Button } from '@mui/material';

function ReviewCommitteeDashboard() {
    const [nominees, setNominees] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data } = await axios.get('http://127.0.0.1:5000/review/dashboard', {
                    headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` }
                });
                setNominees(data.all_nominees_nominator || []); // Ensure the data structure aligns with what is expected
            } catch (error) {
                console.error('Failed to fetch data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <Paper style={{ padding: '20px', margin: '20px' }}>
            <Typography variant="h4" gutterBottom>Review Committee Dashboard</Typography>
            <Button variant="contained" color="primary" onClick={() => navigate('/dashboard')} style={{ marginBottom: '20px' }}>
                Back to Main Dashboard
            </Button>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Nominee Name</TableCell>
                            <TableCell>Department</TableCell>
                            <TableCell>Job Category</TableCell>
                            <TableCell>Details</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {nominees.map((nominee) => (
                            <TableRow key={nominee.id}>
                                <TableCell>{nominee.first_name} {nominee.last_name}</TableCell>
                                <TableCell>{nominee.department_name}</TableCell>
                                <TableCell>{nominee.job_category}</TableCell>
                                <TableCell>
                                    <Button color="primary" onClick={() => navigate(`/review/nominee/${nominee.id}/details`)}>
                                        View Details
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Paper>
    );
}

export default ReviewCommitteeDashboard;
