import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const [formData, setFormData] = useState({
    // Initialize your form data state here
  });

  useEffect(() => {
    // Make POST request to create nominee
    fetch('http://127.0.0.1:5000/nominee/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json', // Fix typo in Content-Type
      },
      body: JSON.stringify(formData),
    })
      .then((resp) => {
        if (!resp.ok) {
          throw new Error('Failed to create nominee');
        }
        // Log the response body
        return resp.text(); // Get response body as text
      })
      .catch((error) => console.error(error));
  }, [formData]); // Add formData to dependency array to trigger useEffect when it changes

  // Handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Optional: Add form validation logic here
    // For example, ensure all required fields are filled
  };

  return (
    <div className="App">
      <h1>Flask and React JS Course</h1>
      <form onSubmit={handleSubmit}>
        {/* Your form inputs */}
        {/* Example: */}
        <input
          type="text"
          name="name"
          placeholder="Nominee Name"
          value={formData.name || ''}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
