import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Assuming your API returns the user's first name at the endpoint `/user/profile`
    // Ensure this API call is authenticated if necessary
    fetch('http://127.0.0.1:5000/user/profile', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('authToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      if(data.first_name) {
        setUserName(data.first_name);
      } else {
        throw new Error('User not found');
      }
    })
    .catch(error => {
      console.error('Error:', error);
      navigate('/'); // Redirect to login if the user data cannot be fetched
    });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Clear the stored token on logout
    navigate('/'); // Redirect to the login page
  };

  return (
    <div>
      <h1>Welcome to the Dashboard</h1>
      <div className="d-flex justify-content-around">
        <h2>Welcome, {userName || ' '}!</h2> {/* Display the fetched user name or 'User' if not set */}
      </div>
      <button onClick={handleLogout} className="btn btn-danger">Logout</button>
      <div className="d-flex justify-content-center">
        <h1><Link to="/nominee/dashboard">Nominator Dashboard</Link></h1>
        <h1><Link to="/recommender/dashboard">Recommender Dashboard</Link></h1>
        <h1><Link to="/review/dashboard">Review Committee Dashboard</Link></h1>
      </div>
    </div>
  );
};

export default Dashboard;
