import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const NewNomineeForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    department_name: '',
    job_category: '',
    email: '',
    nominator_qualification: ''
  });

  const { authToken, userId } = useAuth(); // Assuming `userId` is stored in your Auth context

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    const dataToSend = {
      ...formData,
      user_id: userId // Include the userId in the data sent to the server
    };
    axios.post('http://127.0.0.1:5000/nominee/create', dataToSend, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${authToken}`
      }
    })
    .then(response => {
      // Handle success
      console.log('Nominee created:', response.data);
      // Redirect or show a success message
    })
    .catch(error => {
      // Handle error
      console.error('Error creating nominee:', error.response.data);
      // Show an error message to the user
    });
  };

  return (
    <div className="container">
      <div className="col-14 p-2">
        <legend className="d-flex justify-content-around">New Nominee Form Information</legend>
      </div>
      <div className="d-flex justify-content-center">
        <form onSubmit={handleSubmit} className="bg-light text-dark">
          {/* Form fields */}
          {/* Repeat for each field */}
          <div className="form-group">
            <label>Nominee First Name</label>
            <input
              type="text"
              name="first_name"
              value={formData.first_name}
              onChange={handleChange}
              className="form-control"
              placeholder="Nominee First Name"
            />
          </div>
          {/* Other fields */}
          <div className="form-group">
            <button type="submit" className="btn btn-success">Submit New Nominee</button>
          </div>
        </form>
      </div>
      <div className="col-md-4">
        <a href="/nominee/dashboard" className="btn btn-warning">Back</a>
      </div>
    </div>
  );
};

export default NewNomineeForm;
