import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [firstName, setFirstName] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Assuming you have an endpoint to fetch user data
        const response = await axios.get('http://127.0.0.1:5000/user/data');
        const { fullName } = response.data;

        // Splitting the full name to extract the first name
        const firstName = fullName.split(' ')[0];
        setFirstName(firstName);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container">
      <div className="row">
        <div className="d-flex justify-content-around">
          <h2>Welcome: {firstName}</h2>
        </div>
      </div>

      <a className="text-danger" href="/user/logout"> Logout </a>

      <div className="d-flex justify-content-center">
        <h1><a href="/nominee/dashboard"> Nominator Dashboard </a></h1>
      </div>

      <div className="d-flex justify-content-center">
        <h1><a href="/recommender/dashboard"> Recommender Dashboard</a></h1>
      </div>

      <div className="d-flex justify-content-center">
        <h1><a href="/review/dashboard"> Review Committee Dashboard</a></h1>
      </div>
    </div>
  );
}

export default Dashboard;
