import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Link as RouterLink, Container, Typography, Button, CircularProgress } from '@mui/material';
import axios from 'axios';

function ViewNominee() {
    const [nominee, setNominee] = useState(null);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://127.0.0.1:5000/nominee/${id}/info`)
            .then(response => {
                setNominee(response.data);
            })
            .catch(error => {
                console.error('Error fetching nominee details:', error);
                navigate('/nominee/dashboard'); // Redirect if the fetch fails
            });
    }, [id, navigate]);

    if (!nominee) {
        return (
            <Container>
                <CircularProgress /> {/* MUI CircularProgress for loading indicator */}
            </Container>
        );
    }

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                {nominee.first_name} {nominee.last_name}
            </Typography>
            <Typography variant="subtitle1">
                Department: {nominee.department_name}
            </Typography>
            <Typography variant="subtitle1">
                Job Category: {nominee.job_category}
            </Typography>
            <Typography variant="subtitle1">
                Email: {nominee.email}
            </Typography>
            <Typography variant="subtitle1" gutterBottom>
                Nominator Qualification: {nominee.nominator_qualification}
            </Typography>
            <Button variant="contained" component={RouterLink} to="/nominee/dashboard" style={{ marginTop: '20px' }}>
                Back to Dashboard
            </Button>
        </Container>
    );
}

export default ViewNominee;
